import { useState ,useCallback} from 'react';
import { useParams } from 'react-router-dom';
import useHttp from '../../hooks/use-http';
import { getAllComments } from '../../lib/api';
import LoadingSpinner from '../UI/LoadingSpinner';
import classes from './Comments.module.css';
import NewCommentForm from './NewCommentForm';
import CommentsList from './CommentsList';
const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);
  const params = useParams();
  const {quoutId}= params;
  const {sendRequest  , status , data:LoadedComment}=useHttp(getAllComments);


  const addedCommentHandler = useCallback(() => {
    sendRequest(quoutId);
  }, [sendRequest, quoutId]);

  let comments;

  if (status === 'pending') {
    comments = (
      <div className='centered'>
        <LoadingSpinner />
      </div>
    );
  }

  if (status === 'completed' && LoadedComment && LoadedComment.length > 0) {
    comments = <CommentsList comments={LoadedComment} />;
  }

  if (
    status === 'completed' &&
    (!LoadedComment || LoadedComment.length === 0)
  ) {
    comments = <p className='centered'>No comments were added yet!</p>;
  }


  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };

  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className='btn' onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && <NewCommentForm quoutId={params.quoutId}  onAddedComment={addedCommentHandler}/>}
      {comments}
    </section>
  );
};

export default Comments;
