import { Fragment } from "react";
import { Route, useParams , Link, useRouteMatch } from "react-router-dom";
import Comments from '../components/comments/Comments';
import HighlightedQuote from '../components/quotes/HighlightedQuote';
const  DUMMY_LIST =[
    {id:'q1' , auther:'Max' , text:'LEarn React By Fan.'},
    {id:'q2' , auther:'Enas' , text:'LEarn React By Great.'},
];
const QuoutDetail=()=>{
    useRouteMatch();
     const params=useParams();
    const quote = DUMMY_LIST.find(quote => quote.id === params.quoutId);
    if(!quote){
        return <p> No quote found</p>;
    }
    return <Fragment>
        <HighlightedQuote text={quote.text} author={quote.auther}/>
        <Route path={`/allQuouts/${params.quoutId}`} exact>
            <div className="centered">
                <Link className="btn--flat" to={`/allQuouts/${params.quoutId}/comments`}> Load Comments</Link>
            </div>
        </Route>
        <Route path={`/allQuouts/${params.quoutId}/comments`}>
            <Comments/>
        </Route>
    </Fragment>
}
export default QuoutDetail;
